import style from './Detail.module.css';
import {useParams} from 'react-router-dom';
import { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getById, cleanDetail } from '../../redux/actions';


const Detail = () => {
    const {id} = useParams();
    const dispatch = useDispatch();
    const {name, minWeight, maxWeight, minHeight, maxHeight, minLifeSpan, maxLifeSpan, temperament, image } = useSelector(state => state.dogDetail);
    
    useEffect(()=>{
        dispatch(getById(id))
        return ()=> dispatch(cleanDetail())
    }, [])


    return (
        <>
            <img src={image} alt={name} />
            <div>
                <h3>{name}</h3>
                <h5>Weight: {minWeight} - {maxWeight} kg</h5>
                <h5>Height: {minHeight} - {maxHeight} cm</h5>
                <h5>Life Span: {minLifeSpan} - {maxLifeSpan} years</h5>
                <p>Temperament: {temperament}</p>
            </div>
        </>
    )
}


export default Detail;
