import React, {useEffect, useState} from "react";
import { useDispatch, useSelector } from "react-redux";
import { getDogs } from "../../redux/actions";
import Card from "../Card/Card";
import style from "./CardsContainer.module.css";
import usePagination from "../Pagination/Pagination";

const CardsContainer = () => {
   const dispatch = useDispatch();
   const allDogs = useSelector((state) => state.dogs);
   const { currentItems, pages, currentPage, setCurrentPage } = usePagination(
      allDogs,
      9
    );
   const [pagesToShow] = useState(5);
   const pagesRendered = [];
   let minPage = Math.max(currentPage - Math.floor(pagesToShow / 2), 1);
   let maxPage = Math.min(currentPage + Math.floor(pagesToShow / 2), totalPages);
   
   if (maxPage - minPage < pagesToShow) {
      if (currentPage < pages / 2) {
        maxPage = Math.min(minPage + pagesToShow - 1, pages);
      } else {
        minPage = Math.max(maxPage - pagesToShow + 1, 1);
      }
    }

   for (let i = minPage; i <= maxPage; i++) {
      pagesRendered.push(i);
   }

   useEffect(() => {
      dispatch(getDogs());
   }, []);


   return (
      <>
         <ul className={style.container}>
            {currentItems.length 
            ? (currentItems.map((dog) => {
               return (
                  <li key={dog.id}>
                     <Card 
                        key={dog.id} 
                        id={dog.id} 
                        name={dog.name}
                        image={dog.image}
                        temperament={dog.temperament}
                     />
                  </li>)
            }))
            : null
            }
         </ul>
         
         <div className={style.pages}>
            <button onClick={() => setCurrentPage(currentPage - 1)} disabled={currentPage === 1}>
               ◄
            </button>
            {pagesRendered.map((page) => {
               return (
                  <button
                     key={page}
                     onClick={() => setCurrentPage(page)}
                     className={page === currentPage ? style.active : ""}
                  >
                     {page + 1}
                  </button>
               );
            })}

            <button onClick={() => setCurrentPage(currentPage + 1)} disabled={currentPage === pages}>
               ►
            </button>
         </div>
      </>
   )

}


export default CardsContainer;
