import React, {useEffect, useState} from "react";
import { useDispatch, useSelector } from "react-redux";
import { getDogs } from "../../redux/actions";
import Card from "../Card/Card";
import style from "./CardsContainer.module.css";
import usePagination from "../Pagination/Pagination";

const CardsContainer = () => {
   const dispatch = useDispatch();
   const allDogs = useSelector((state) => state.dogs);
   const { currentItems, pages, currentPage, setCurrentPage } = usePagination(
      allDogs,
      9
    );
   
   const renderPages = () => {
      const pageRange = 2; 
      const pagesRendered = [];

      for (let page = currentPage - pageRange; page <= currentPage + pageRange; page++) {
        if (page >= 1 && page <= pages) {
         pagesRendered.push(
            <button
              key={page}
              className={`${currentPage === page ? style.activePage : ''}`}
              onClick={() => setCurrentPage(page)}
            >
              {page}
            </button>
          );
        }
      }
  
      return pagesRendered;
    };
  
   const handlePreviousClick = () => {
      if (currentPage > 1) {
        setCurrentPage(currentPage - 1);
      }
    };
  
   const handleNextClick = () => {
      if (currentPage < pages) {
        setCurrentPage(currentPage + 1);
      }
   };

   useEffect(() => {
      dispatch(getDogs());
   }, []);


   return (
      <>
         <ul className={style.container}>
            {currentItems.length 
            ? (currentItems.map((dog) => {
               return (
                  <li key={dog.id}>
                     <Card 
                        key={dog.id} 
                        id={dog.id} 
                        name={dog.name}
                        image={dog.image}
                        temperament={dog.temperament}
                     />
                  </li>)
            }))
            : null
            }
         </ul>
         
         <div className={style.pages}>
            <button onClick={handlePreviousClick} disabled={currentPage === 1}>
               ◄
            </button>
            {pages.map((page, index) => {
               return (
                  <button
                     key={index}
                     onClick={() => setCurrentPage(page)}
                     className={page === currentPage ? styles.active : ""}
                  >
                     {page}
                  </button>
               );
            })}
            <button onClick={handleNextClick} disabled={currentPage === pages}>
               ►
            </button>
         </div>
      </>
   )

}


export default CardsContainer;
